settings.css:
.mapplic-thumbnail > img {
	filter: grayscale(100%);
	opacity: 0.5;
}

.mapplic-dir-item:hover .mapplic-thumbnail img,
.mapplic-dir-item.mapplic-active .mapplic-thumbnail img {
	filter: grayscale(0%);
	opacity: 1;
}

.mapplic-thumbnail > img {
    object-fit: contain;
}

.mapplic-popup-image > img {
    object-fit: contain;
    padding: 8px;
    box-sizing: border-box;
}
